INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (1,NULL,1365,'Fundação de Acordo.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (2,NULL,1389,'3a tentativa de invasão de Âmiem.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (3,NULL,1390,'Trégua entre os Senhores Infernais. Morte de Alvór XI da Moldânia. Os demonistas conseguem invadir Âmiem.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (4,NULL,1391,'Fuga de Malim e corajosos aventureiros para Acordo. Plana é subjugada ao poder demonista.');
