INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (6,NULL,1393,'Inicio da Jornada pela Unificação.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (7,NULL,1402,'Morrigalti é vencido em Âmiem, ele é o 1o Príncipe do Inferno derrotado.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (8,NULL,1403,'Batalha dos Mil Mártires (em Azanti) resgate da pedra negra.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (9,NULL,1405,'Unificação. A Batalha Final acontece e se dá a Revoada dos Demônios (expulsão).');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (10,NULL,1406,'O Norte se liberta da dominação Bankdi, Filanti assume o controle de Azanti.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (11,NULL,1407,'O Sul se Liberta da Dominação bankdi.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (12,NULL,1420,'Invasão dos Dragões em Abadom.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (13,NULL,1449,'Morte do Mais Sábio.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (14,NULL,1450,'Fim da Unificação.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (15,NULL,1451,'Grande parte das nações, começando por Verrogar, declararam sua independência de Saravossa.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (16,NULL,1452,'A Moldania se separa em duas nações, Calco e Conti.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (17,NULL,1468,'Fundação da Magocracia de Portis.');
INSERT INTO [linha_tempo] ([id],[id_localidade],[ano],[evento]) VALUES (18,NULL,1495,'Inicio da guerra entre Verrogar e Dantsem, quando Verrogar conquista um acesso ao mar.');
